import React, { useEffect, useState } from "react";
import axios from "axios";
import TopNav from "../components/TopNav";

const ManageLocations = () => {
  const [cities, setCities] = useState([]);
  const [cityName, setCityName] = useState("");

  const [hospitalName, setHospitalName] = useState("");
  const [hospitalAddress, setHospitalAddress] = useState("");
  const [selectedCityId, setSelectedCityId] = useState("");
  const [hospitalList, setHospitalList] = useState([]);

  // Load cities and hospitals on mount
  useEffect(() => {
    fetchCities();
    fetchHospitals();
  }, []);

  const fetchCities = () => {
    axios.get("http://localhost:8080/api/cities")
      .then(res => setCities(res.data))
      .catch(err => console.error("Error loading cities:", err));
  };

  const fetchHospitals = () => {
    axios.get("http://localhost:8080/api/hospitals")
      .then(res => setHospitalList(res.data))
      .catch(err => console.error("Error loading hospitals:", err));
  };

  const handleAddCity = () => {
    if (!cityName.trim()) return;
    axios.post("http://localhost:8080/api/cities/add", { name: cityName })
      .then(() => {
        setCityName("");
        fetchCities();
      })
      .catch(err => console.error("Error adding city:", err));
  };

  const handleAddHospital = () => {
    if (!hospitalName || !hospitalAddress || !selectedCityId) return;
    axios.post("http://localhost:8080/api/hospitals/add", {
      name: hospitalName,
      address: hospitalAddress,
      city: { id: selectedCityId }
    })
      .then(() => {
        setHospitalName("");
        setHospitalAddress("");
        setSelectedCityId("");
        fetchHospitals();
      })
      .catch(err => console.error("Error adding hospital:", err));
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-2">AMSHC</h1>
      <TopNav />

      <div className="max-w-4xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg mt-6 space-y-8">
        <h2 className="text-2xl font-semibold">üèôÔ∏è Manage Cities & Hospitals</h2>

        {/* Add City */}
        <div>
          <h3 className="text-xl font-semibold mb-2">‚ûï Add City</h3>
          <div className="flex gap-4">
            <input
              type="text"
              placeholder="Enter city name"
              value={cityName}
              onChange={(e) => setCityName(e.target.value)}
              className="w-full p-3 rounded-md text-black"
            />
            <button
              onClick={handleAddCity}
              className="bg-green-500 hover:bg-green-400 text-white px-6 py-2 rounded-lg font-semibold"
            >
              Add City
            </button>
          </div>
        </div>

        {/* Add Hospital */}
        <div>
          <h3 className="text-xl font-semibold mb-2">üè• Add Hospital</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input
              type="text"
              placeholder="Hospital Name"
              value={hospitalName}
              onChange={(e) => setHospitalName(e.target.value)}
              className="p-3 rounded-md text-black"
            />
            <input
              type="text"
              placeholder="Address"
              value={hospitalAddress}
              onChange={(e) => setHospitalAddress(e.target.value)}
              className="p-3 rounded-md text-black"
            />
            <select
              value={selectedCityId}
              onChange={(e) => setSelectedCityId(e.target.value)}
              className="p-3 rounded-md text-black"
            >
              <option value="">-- Select City --</option>
              {cities.map(city => (
                <option key={city.id} value={city.id}>{city.name}</option>
              ))}
            </select>
            <button
              onClick={handleAddHospital}
              className="bg-green-500 hover:bg-green-400 text-white px-6 py-2 rounded-lg font-semibold"
            >
              Add Hospital
            </button>
          </div>
        </div>

        {/* List Hospitals */}
        <div>
          <h3 className="text-xl font-semibold mb-2">üìã Existing Hospitals</h3>
          <div className="space-y-3">
            {hospitalList.map(hospital => (
              <div key={hospital.id} className="p-4 bg-blue-700 rounded-md">
                <h4 className="font-bold">{hospital.name}</h4>
                <p>{hospital.address}</p>
                <p className="text-sm text-blue-200">City: {hospital.city?.name || "Unknown"}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ManageLocations;
