import React, { useState, useEffect } from "react";
import axios from "axios";
import TopNav from "../components/TopNav";

const Doctor = () => {
  const [doctors, setDoctors] = useState([]);
  const [name, setName] = useState("");
  const [specialization, setSpecialization] = useState("");
  const [experienceYears, setExperienceYears] = useState("");
  const [gender, setGender] = useState("Male");
  const [clinicName, setClinicName] = useState("");
  const [editingDoctorId, setEditingDoctorId] = useState(null);

  useEffect(() => {
    fetchDoctors();
  }, []);

  const fetchDoctors = () => {
    axios.get("http://localhost:8080/api/doctors")
      .then(res => setDoctors(res.data))
      .catch(err => console.error("Error fetching doctors", err));
  };

  const resetForm = () => {
    setName("");
    setSpecialization("");
    setExperienceYears("");
    setGender("Male");
    setClinicName("");
    setEditingDoctorId(null);
  };

  const handleAddOrUpdateDoctor = async () => {
    if (!name || !specialization || !experienceYears || !clinicName) return;

    try {
      const hospitals = await axios.get("http://localhost:8080/api/hospitals");
      const hospital = hospitals.data.find(h => h.name.toLowerCase() === clinicName.toLowerCase());

      if (!hospital) {
        alert("Hospital not found. Please make sure the name matches exactly.");
        return;
      }

      const payload = {
        name,
        specialization,
        experienceYears,
        gender,
        hospital: { id: hospital.id }
      };

      if (editingDoctorId) {
        await axios.put(`http://localhost:8080/api/doctors/update/${editingDoctorId}`, payload);
      } else {
        await axios.post("http://localhost:8080/api/doctors/add", payload);
      }

      resetForm();
      fetchDoctors();

    } catch (err) {
      console.error("Error saving doctor:", err);
    }
  };

  const handleEdit = (doctor) => {
    setName(doctor.name);
    setSpecialization(doctor.specialization);
    setExperienceYears(doctor.experienceYears);
    setGender(doctor.gender || "Male");
    setClinicName(doctor.hospital?.name || "");
    setEditingDoctorId(doctor.id);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/doctors/delete/${id}`);
      fetchDoctors();
    } catch (err) {
      console.error("Error deleting doctor", err);
    }
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-2">AMSHC</h1>
      <TopNav />

      <div className="max-w-3xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg mt-6">
        <h2 className="text-2xl font-semibold text-center mb-6">
          {editingDoctorId ? "Edit Doctor" : "Manage Doctors"}
        </h2>

        {/* Form */}
        <div className="space-y-4 mb-8">
          <input
            type="text"
            placeholder="Doctor Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Specialization"
            value={specialization}
            onChange={(e) => setSpecialization(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Experience (e.g., 10 years)"
            value={experienceYears}
            onChange={(e) => setExperienceYears(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <select
            value={gender}
            onChange={(e) => setGender(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          >
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
          <input
            type="text"
            placeholder="Clinic Name (must match existing hospital)"
            value={clinicName}
            onChange={(e) => setClinicName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />

          <div className="flex gap-2">
            <button
              onClick={handleAddOrUpdateDoctor}
              className="bg-green-500 hover:bg-green-400 text-white px-6 py-3 rounded-lg font-bold w-full"
            >
              {editingDoctorId ? "Update Doctor" : "Add Doctor"}
            </button>
            {editingDoctorId && (
              <button
                onClick={resetForm}
                className="bg-gray-500 hover:bg-gray-400 text-white px-6 py-3 rounded-lg font-bold w-full"
              >
                Cancel Edit
              </button>
            )}
          </div>
        </div>

        {/* Doctor List */}
        <div className="space-y-4">
          {doctors.map((doctor) => {
            const doctorPhoto = doctor.gender === "Female"
              ? "/doctor_images/female_doctor.png"
              : "/doctor_images/male_doctor.png";

            return (
              <div key={doctor.id} className="flex items-center justify-between bg-blue-700 p-4 rounded-md shadow-md">
                <div className="flex items-center gap-4">
                  <img src={doctorPhoto} alt={doctor.name} className="w-16 h-16 rounded-full object-cover" />
                  <div>
                    <h3 className="text-lg font-bold">{doctor.name}</h3>
                    <p>{doctor.specialization}</p>
                    <p className="text-sm text-blue-200">Clinic: {doctor.hospital?.name || "N/A"}</p>
                  </div>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => handleEdit(doctor)}
                    className="bg-yellow-400 hover:bg-yellow-300 text-black px-4 py-1 rounded-md"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(doctor.id)}
                    className="bg-red-500 hover:bg-red-400 text-white px-4 py-1 rounded-md"
                  >
                    Delete
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Doctor;
