import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { CalendarDays, User, ClipboardList, History } from 'lucide-react';

const TopNav = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Hide TopNav on login and register pages
  if (location.pathname === '/login' || location.pathname === '/register') {
    return null;
  }

  return (
    <div className="bg-blue-900 py-4 px-4 shadow-md text-white">
      <div className="text-center font-semibold text-lg mb-2">Appointment</div>
      <div className="flex justify-around items-center space-x-4 text-sm">
        <button
          className="flex flex-col items-center hover:text-blue-300"
          onClick={() => navigate('/booking')}
        >
          <CalendarDays size={28} />
          <span>Book now</span>
        </button>
        <button
          className="flex flex-col items-center hover:text-blue-300"
          onClick={() => navigate('/booking#doctor')}
        >
          <User size={28} />
          <span>Choose Doctor</span>
        </button>
        <button
          className="flex flex-col items-center hover:text-blue-300"
          onClick={() => navigate('/token')}
        >
          <ClipboardList size={28} />
          <span>Token NO.</span>
        </button>
        <button
          className="flex flex-col items-center hover:text-blue-300"
          onClick={() => navigate('/history')}
        >
          <History size={28} />
          <span>Booking History</span>
        </button>
      </div>
    </div>
  );
};

export default TopNav;
