// src/components/BottomNav.js
import React from "react";
import { Link, useLocation } from "react-router-dom";
import { FaCalendarAlt, FaUserMd, FaClipboardList, FaHistory } from "react-icons/fa";

const BottomNav = () => {
  const location = useLocation();
  const hideOnRoutes = ["/login", "/register"];

  if (hideOnRoutes.includes(location.pathname)) return null;

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-blue-900 text-white flex justify-around items-center py-3 shadow-md z-50">
      <Link to="/booking" className="flex flex-col items-center hover:text-blue-300">
        <FaCalendarAlt size={24} />
        <span className="text-sm mt-1">Book Now</span>
      </Link>
      <Link to="/booking" className="flex flex-col items-center hover:text-blue-300">
        <FaUserMd size={24} />
        <span className="text-sm mt-1">Choose Doctor</span>
      </Link>
      <Link to="/token" className="flex flex-col items-center hover:text-blue-300">
        <FaClipboardList size={24} />
        <span className="text-sm mt-1">Token No.</span>
      </Link>
      <Link to="/history" className="flex flex-col items-center hover:text-blue-300">
        <FaHistory size={24} />
        <span className="text-sm mt-1">Booking History</span>
      </Link>
    </nav>
  );
};

export default BottomNav;
