import React from 'react';
import { useNavigate } from 'react-router-dom';
import TopNav from '../components/TopNav'; // Make sure this path is correct

const Dashboard = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-800 to-blue-900 text-white px-4 py-6">
      <h1 className="text-3xl font-bold text-center mb-2">AMSHC</h1>
      <TopNav /> {/* ðŸ‘ˆ TopNav appears below the title */}

      <div className="flex flex-col items-center justify-center mt-10">
        <h2 className="text-4xl font-bold mb-4 text-center">Welcome to AMSHC</h2>
        <p className="text-lg text-center mb-8 max-w-xl">
          Book your appointment easily at clinics and hospitals near you. Skip the hassle and connect with doctors quickly and conveniently.
        </p>

        <div className="bg-blue-800 p-8 rounded-xl shadow-lg w-full max-w-md text-center">
          <h3 className="text-2xl font-semibold mb-4">Start Here</h3>
          <button
            onClick={() => navigate('/booking')}
            className="px-8 py-3 bg-white hover:bg-gray-100 text-blue-900 rounded-lg font-semibold flex items-center justify-center gap-2"
          >
            ðŸ“… Book Appointment
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
