// ✅ src/pages/Dashboard.js
import React, { useState } from "react";

const cityData = {
  "Quezon City": [
    { name: "St. Luke's Medical Center", address: "279 E Rodriguez Sr. Ave" },
    { name: "East Avenue Medical Center", address: "East Avenue" },
    { name: "Capitol Medical Center", address: "Quezon Ave" },
  ],
  "Manila": [
    { name: "Manila Doctors Hospital", address: "UN Ave" },
    { name: "Philippine General Hospital", address: "Taft Ave" },
    { name: "Ospital ng Maynila", address: "Malate" },
  ],
  "Cebu City": [
    { name: "Chong Hua Hospital", address: "Fuente Osmeña" },
    { name: "Cebu Doctors University Hospital", address: "Capitol Site" },
    { name: "Vicente Sotto Memorial Medical Center", address: "B. Rodriguez" },
  ],
  "Davao City": [
    { name: "Davao Doctors Hospital", address: "E Quirino Ave" },
    { name: "Southern Philippines Medical Center", address: "JP Laurel" },
    { name: "San Pedro Hospital", address: "C Guzman" },
  ],
  "Caloocan": [
    { name: "Our Lady of Grace Hospital", address: "11th Ave" },
    { name: "Caloocan City Medical Center", address: "A. Mabini St." },
    { name: "Dr. Jose N. Rodriguez Memorial Hospital", address: "Tala" },
  ],
  "Zamboanga City": [
    { name: "Zamboanga Peninsula Hospital", address: "Veterans Ave" },
    { name: "Brent Hospital", address: "R.T. Lim Blvd" },
    { name: "Zamboanga City Medical Center", address: "Sta. Catalina" },
  ],
  "Taguig": [
    { name: "St. Luke's Medical Center - BGC", address: "Bonifacio Global City" },
    { name: "Taguig Pateros District Hospital", address: "Napindan" },
    { name: "Medical Center Taguig", address: "Bicutan" },
  ],
  "Pasig": [
    { name: "The Medical City", address: "Ortigas Ave" },
    { name: "Pasig City General Hospital", address: "Nagpayong" },
    { name: "VRP Medical Center", address: "Shaw Blvd" },
  ],
  "Antipolo": [
    { name: "Antipolo City Medical Hospital", address: "M.L. Quezon Ext" },
    { name: "Unciano Medical Center", address: "Circumferential Rd" },
    { name: "Metro Antipolo Hospital", address: "Sumulong Hwy" },
  ],
  "Cagayan de Oro": [
    { name: "Northern Mindanao Medical Center", address: "Capistrano St" },
    { name: "Polymedic Medical Plaza", address: "Kauswagan" },
    { name: "Capitol University Medical Center", address: "Corrales Ext" },
  ],
};

export default function Dashboard() {
  const [selectedCity, setSelectedCity] = useState(null);

  const handleCitySelect = (city) => {
    setSelectedCity(city);
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-2xl font-bold mb-4">Select City</h1>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
        {Object.keys(cityData).map((city) => (
          <button
            key={city}
            onClick={() => handleCitySelect(city)}
            className={`px-4 py-2 rounded-lg border font-medium shadow-sm hover:bg-blue-100 transition ${
              selectedCity === city ? "bg-blue-500 text-white" : "bg-white"
            }`}
          >
            {city}
          </button>
        ))}
      </div>

      {selectedCity && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-3">Hospitals in {selectedCity}</h2>
          <ul className="space-y-3">
            {cityData[selectedCity].map((clinic, index) => (
              <li key={index} className="p-4 bg-white rounded-lg shadow">
                <h3 className="text-lg font-bold">{clinic.name}</h3>
                <p className="text-sm text-gray-600">{clinic.address}</p>
                <button className="mt-2 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition">
                  Select Doctor
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
