// src/pages/Dashboard.js
import React, { useState } from "react";

const clinics = [
  { name: "Covid Clinic", address: "Cebu City", zip: "6000" },
  { name: "Govt. Hospital", address: "Cebu City", zip: "6000" },
  { name: "MemorialCare", address: "Cebu City", zip: "6000" }
];

const Dashboard = () => {
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [patientType, setPatientType] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedClinic || !patientType || !patientName || !contactNumber) {
      alert("Please complete all fields.");
      return;
    }
    const appointmentData = {
      clinic: selectedClinic,
      patientType,
      patientName,
      contactNumber
    };
    console.log("Appointment Data:", appointmentData);
    alert("Appointment successfully submitted!");
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-8">AMSHC</h1>
      <div className="max-w-4xl mx-auto bg-blue-800 p-6 rounded-xl shadow-lg">
        <h2 className="text-xl font-semibold mb-4">Where would you like to meet?</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          {clinics.map((clinic, idx) => (
            <button
              key={idx}
              onClick={() => setSelectedClinic(clinic.name)}
              className={`px-4 py-2 rounded-full text-white w-full transition ${
                selectedClinic === clinic.name ? "bg-blue-300 text-blue-900 font-bold" : "bg-blue-500 hover:bg-blue-600"
              }`}
            >
              {clinic.name} <br /><span className="text-sm">{clinic.address}, {clinic.zip}</span>
            </button>
          ))}
        </div>

        <h2 className="text-xl font-semibold mb-4">Who is this patient?</h2>
        <div className="flex gap-4 mb-6 justify-center">
          {["Myself", "My child", "My wife"].map((type, idx) => (
            <button
              key={idx}
              onClick={() => setPatientType(type)}
              className={`px-4 py-2 rounded-lg transition ${
                patientType === type ? "bg-blue-300 text-blue-900 font-bold" : "bg-blue-600 hover:bg-blue-500"
              }`}
            >
              {type}
            </button>
          ))}
        </div>

        <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4">
          <input
            type="text"
            placeholder="Patient Name"
            className="px-4 py-2 rounded text-black"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            required
          />
          <input
            type="tel"
            placeholder="Contact Number"
            className="px-4 py-2 rounded text-black"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            required
          />
          <button
            type="submit"
            className="bg-white text-blue-700 py-2 rounded-lg hover:bg-blue-100"
          >
            Submit Appointment
          </button>
        </form>
      </div>
    </div>
  );
};

export default Dashboard;
