import React, { useState } from "react";

// Sample data
const topCities = [
  "Quezon City", "Manila", "Davao City", "Caloocan", "Cebu City",
  "Zamboanga City", "Taguig", "Antipolo", "Pasig", "Cagayan de Oro"
];

const clinicsPerCity = {
  "Quezon City": ["St. Luke's", "East Avenue Med Center", "Quezon City General Hospital"],
  "Manila": ["PGH", "Manila Doctors", "ManilaMed"],
  "Davao City": ["Davao Medical Center", "San Pedro Hospital", "Brokenshire Hospital"],
  "Caloocan": ["Caloocan Med", "Our Lady of Grace", "Martinez Hospital"],
  "Cebu City": ["Chong Hua", "Cebu Doctors", "Vicente Sotto"],
  "Zamboanga City": ["Peninsula Hosp", "Brent Hosp", "Ciudad Med"],
  "Taguig": ["St. Luke's BGC", "Taguig Gen", "Med Center Taguig"],
  "Antipolo": ["Antipolo Doctors", "Unciano", "Antipolo MedCare"],
  "Pasig": ["The Medical City", "Pasig Gen", "Pasig Doctors"],
  "Cagayan de Oro": ["Northern Mindanao", "Polymedic", "Capitol Univ Med"]
};

// Generate doctors for each clinic
const doctorsPerClinic = {};
Object.values(clinicsPerCity).flat().forEach((clinic) => {
  doctorsPerClinic[clinic] = [
    { name: `Dr. ${clinic} A`, specialization: "Cardiologist", history: "10 years experience" },
    { name: `Dr. ${clinic} B`, specialization: "Dentist", history: "6 years experience" },
    { name: `Dr. ${clinic} C`, specialization: "Pediatrician", history: "4 years experience" }
  ];
});

// Replace with your own API key or use static image fallback
const getMapUrl = (city) =>
  `https://maps.googleapis.com/maps/api/staticmap?center=${encodeURIComponent(
    city
  )}&zoom=12&size=600x300&markers=color:red%7C${encodeURIComponent(city)}&key=YOUR_GOOGLE_STATIC_MAPS_API_KEY`;

const Dashboard = () => {
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedClinic, setSelectedClinic] = useState("");
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [confirmationMessage, setConfirmationMessage] = useState("");

  const handleConfirm = () => {
    const appointment = {
      city: selectedCity,
      clinic: selectedClinic,
      doctor: selectedDoctor,
      patientName,
      contactNumber
    };
    console.log("ðŸ“‹ Appointment Booked:", appointment);
    setConfirmationMessage("âœ… Appointment confirmed successfully!");
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <div className="text-center text-3xl font-bold mb-8">AMSHC</div>

      <div className="max-w-6xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
          {/* Map & Clinics */}
          <div>
            <h2 className="text-xl font-semibold mb-2">Where would you like to meet?</h2>
            <div className="mb-4 border rounded-md overflow-hidden">
              {selectedCity ? (
                <img
                  src={getMapUrl(selectedCity)}
                  alt={`Map of ${selectedCity}`}
                  className="w-full"
                />
              ) : (
                <div className="bg-blue-600 h-[200px] flex items-center justify-center text-white text-lg">
                  Select a city to show map
                </div>
              )}
            </div>

            <select
              className="w-full p-3 rounded-md text-black mb-3"
              value={selectedCity}
              onChange={(e) => {
                setSelectedCity(e.target.value);
                setSelectedClinic("");
                setSelectedDoctor(null);
              }}
            >
              <option value="">-- Choose City --</option>
              {topCities.map((city) => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>

            {selectedCity && (
              <div className="space-y-2">
                {clinicsPerCity[selectedCity]?.map((clinic, index) => (
                  <button
                    key={index}
                    onClick={() => {
                      setSelectedClinic(clinic);
                      setSelectedDoctor(null);
                    }}
                    className={`w-full px-4 py-2 rounded-md transition text-left ${
                      selectedClinic === clinic
                        ? "bg-white text-blue-900 font-bold"
                        : "bg-blue-500 hover:bg-blue-400"
                    }`}
                  >
                    {clinic}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Doctor Selection */}
          {selectedClinic && (
            <div>
              <h2 className="text-xl font-semibold mb-3">Choose a Doctor</h2>
              <div className="space-y-3">
                {doctorsPerClinic[selectedClinic]?.map((doctor, index) => (
                  <div
                    key={index}
                    onClick={() => setSelectedDoctor(doctor)}
                    className={`p-4 rounded-md shadow-md cursor-pointer transition border ${
                      selectedDoctor?.name === doctor.name ? "border-white" : "border-transparent"
                    }`}
                  >
                    <h3 className="text-lg font-semibold">{doctor.name}</h3>
                    <p className="text-sm text-gray-200">{doctor.specialization}</p>
                    <p className="text-xs text-gray-300">{doctor.history}</p>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Patient Info */}
        <div className="grid md:grid-cols-2 gap-4 mb-6">
          <input
            type="text"
            placeholder="Patient Name"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
        </div>

        {/* Confirm Button */}
        {selectedDoctor && patientName && contactNumber && (
          <div className="text-center">
            <button
              onClick={handleConfirm}
              className="px-8 py-3 bg-green-500 hover:bg-green-400 text-white rounded-lg font-semibold"
            >
              Confirm Appointment
            </button>
            {confirmationMessage && (
              <p className="mt-4 text-green-300 font-medium">{confirmationMessage}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
