import React, { useState } from "react";

const topCities = [
  "Quezon City", "Manila", "Davao City", "Caloocan", "Cebu City",
  "Zamboanga City", "Taguig", "Antipolo", "Pasig", "Cagayan de Oro"
];

const clinicsPerCity = {
  "Quezon City": [
    {
      name: "St. Luke's Medical Center",
      address: "279 E Rodriguez Sr. Ave, Quezon City, 1112 Metro Manila"
    },
    {
      name: "East Avenue Medical Center",
      address: "East Ave, Diliman, Quezon City, Metro Manila"
    },
    {
      name: "Quezon City General Hospital",
      address: "Seminary Rd, Quezon City, 1106 Metro Manila"
    }
  ],
  "Manila": [
    {
      name: "Philippine General Hospital (PGH)",
      address: "Taft Ave, Ermita, Manila, 1000 Metro Manila"
    },
    {
      name: "Manila Doctors Hospital",
      address: "667 UN Ave, Ermita, Manila, 1000 Metro Manila"
    },
    {
      name: "ManilaMed",
      address: "850 UN Ave, Ermita, Manila, 1000 Metro Manila"
    }
  ],
  "Davao City": [
    {
      name: "Davao Doctors Hospital",
      address: "118 E Quirino Ave, Davao City, 8000 Davao del Sur"
    },
    {
      name: "San Pedro Hospital",
      address: "C. De Guzman St, Davao City, 8000 Davao del Sur"
    },
    {
      name: "Brokenshire Hospital",
      address: "Madapo Hills, Davao City, Davao del Sur"
    }
  ],
  "Caloocan": [
    {
      name: "Caloocan City Medical Center",
      address: "C-4 Rd, Dagat-Dagatan, Caloocan, Metro Manila"
    },
    {
      name: "Our Lady of Grace Hospital",
      address: "8th Ave. Grace Park, Caloocan City, Metro Manila"
    },
    {
      name: "Martinez Memorial Hospital",
      address: "198 Bayan-Bayanan Ave, Caloocan City, Metro Manila"
    }
  ],
  "Cebu City": [
    {
      name: "Chong Hua Hospital",
      address: "Don Mariano Cui St., Capitol Site, Cebu City, 6000 Cebu"
    },
    {
      name: "Cebu Doctors' University Hospital",
      address: "OsmeÃ±a Blvd, Cebu City, 6000 Cebu"
    },
    {
      name: "Vicente Sotto Memorial Medical Center",
      address: "B. Rodriguez St, Cebu City, 6000 Cebu"
    }
  ],
  "Zamboanga City": [
    {
      name: "Zamboanga Peninsula Medical Center",
      address: "Veterans Ave, Zamboanga City, 7000 Zamboanga del Sur"
    },
    {
      name: "Brent Hospital and Colleges Inc.",
      address: "R.T. Lim Blvd, Zamboanga City, 7000"
    },
    {
      name: "Ciudad Medical Zamboanga",
      address: "Nunez Extension, Zamboanga City, 7000"
    }
  ],
  "Taguig": [
    {
      name: "St. Luke's Medical Center â€“ BGC",
      address: "32nd St, Taguig, 1634 Metro Manila"
    },
    {
      name: "Taguig-Pateros District Hospital",
      address: "Levi Mariano Ave, Taguig, Metro Manila"
    },
    {
      name: "Medical Center Taguig",
      address: "Levi Mariano Ave, Ususan, Taguig, Metro Manila"
    }
  ],
  "Antipolo": [
    {
      name: "Antipolo Doctors Hospital",
      address: "Manila East Rd, Antipolo, 1870 Rizal"
    },
    {
      name: "Unciano Medical Center - Antipolo",
      address: "Circumferential Rd, San Roque, Antipolo, Rizal"
    },
    {
      name: "Antipolo MedCare",
      address: "M.L. Quezon Ext., Antipolo, Rizal"
    }
  ],
  "Pasig": [
    {
      name: "The Medical City",
      address: "Ortigas Ave, Pasig, Metro Manila"
    },
    {
      name: "Pasig City General Hospital",
      address: "Caruncho Ave, Pasig, Metro Manila"
    },
    {
      name: "Pasig Doctors Medical Center",
      address: "Mercedes Ave, Pasig, Metro Manila"
    }
  ],
  "Cagayan de Oro": [
    {
      name: "Northern Mindanao Medical Center",
      address: "Capitol Compound, Cagayan de Oro, Misamis Oriental"
    },
    {
      name: "Polymedic Medical Plaza",
      address: "Kauswagan National Highway, Cagayan de Oro City"
    },
    {
      name: "Capitol University Medical Center",
      address: "Corrales Ext, Cagayan de Oro, Misamis Oriental"
    }
  ]
};

const Dashboard = () => {
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [confirmationMessage, setConfirmationMessage] = useState("");

  const handleConfirm = () => {
    const appointment = {
      city: selectedCity,
      clinic: selectedClinic?.name,
      doctor: selectedDoctor,
      patientName,
      contactNumber
    };
    console.log("ðŸ“‹ Appointment Booked:", appointment);
    setConfirmationMessage("âœ… Appointment confirmed successfully!");
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <div className="text-center text-3xl font-bold mb-8">AMSHC</div>

      <div className="max-w-6xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
          {/* City & Clinic Selection */}
          <div>
            <h2 className="text-xl font-semibold mb-2">Select City & Clinic</h2>
            <select
              className="w-full p-3 rounded-md text-black mb-4"
              value={selectedCity}
              onChange={(e) => {
                setSelectedCity(e.target.value);
                setSelectedClinic(null);
                setSelectedDoctor(null);
              }}
            >
              <option value="">-- Choose City --</option>
              {topCities.map((city) => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>

            {selectedCity && (
              <div className="space-y-3">
                {clinicsPerCity[selectedCity]?.map((clinic, index) => (
                  <button
                    key={index}
                    onClick={() => {
                      setSelectedClinic(clinic);
                      setSelectedDoctor(null);
                    }}
                    className={`w-full px-4 py-2 rounded-md transition text-left ${
                      selectedClinic?.name === clinic.name
                        ? "bg-white text-blue-900 font-bold"
                        : "bg-blue-500 hover:bg-blue-400"
                    }`}
                  >
                    {clinic.name}
                  </button>
                ))}
              </div>
            )}

            {selectedClinic && (
              <div className="mt-6">
                <p className="text-lg font-semibold">{selectedClinic.name}</p>
                <p className="text-sm text-gray-200 mb-2">{selectedClinic.address}</p>
                <a
                  href={`https://www.google.com/search?q=${encodeURIComponent(selectedClinic.name)}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-300 underline"
                >
                  ðŸ”— View on Google
                </a>
              </div>
            )}
          </div>

          {/* Doctor Selection */}
          {selectedClinic && (
            <div>
              <h2 className="text-xl font-semibold mb-3">Choose a Doctor</h2>
              <div className="space-y-3">
                {["Dr. Maria Reyes", "Dr. John Cruz", "Dr. Anna Santos"].map((doctorName, index) => (
                  <div
                    key={index}
                    onClick={() =>
                      setSelectedDoctor({
                        name: doctorName,
                        specialization: index === 0 ? "Cardiologist" : index === 1 ? "Dentist" : "Pediatrician",
                        history: `${8 + index} years experience`
                      })
                    }
                    className={`p-4 rounded-md shadow-md cursor-pointer transition border ${
                      selectedDoctor?.name === doctorName ? "border-white" : "border-transparent"
                    }`}
                  >
                    <h3 className="text-lg font-semibold">{doctorName}</h3>
                    <p className="text-sm text-gray-200">
                      {index === 0 ? "Cardiologist" : index === 1 ? "Dentist" : "Pediatrician"}
                    </p>
                    <p className="text-xs text-gray-300">{8 + index} years experience</p>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Patient Info */}
        <div className="grid md:grid-cols-2 gap-4 mb-6">
          <input
            type="text"
            placeholder="Patient Name"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
        </div>

        {/* Confirm Button */}
        {selectedDoctor && patientName && contactNumber && (
          <div className="text-center">
            <button
              onClick={handleConfirm}
              className="px-8 py-3 bg-green-500 hover:bg-green-400 text-white rounded-lg font-semibold"
            >
              Confirm Appointment
            </button>
            {confirmationMessage && (
              <p className="mt-4 text-green-300 font-medium">{confirmationMessage}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
