import React, { useEffect, useState } from "react";
import TopNav from "../components/TopNav";
import axios from "axios";

const Booking = () => {
  const [cities, setCities] = useState([]);
  const [hospitals, setHospitals] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedHospital, setSelectedHospital] = useState("");
  const [doctors, setDoctors] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [confirmationMessage, setConfirmationMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    axios.get("http://localhost:8080/api/cities").then((res) => {
      setCities(res.data);
    });
  }, []);

  useEffect(() => {
    if (selectedCity) {
      axios
        .get(`http://localhost:8080/api/hospitals/city/${selectedCity}`)
        .then((res) => {
          setHospitals(res.data);
        });
    }
  }, [selectedCity]);

  useEffect(() => {
    if (selectedHospital) {
      axios
        .get(`http://localhost:8080/api/doctors/hospital/${selectedHospital}`)
        .then((res) => {
          setDoctors(res.data);
        });
    }
  }, [selectedHospital]);

  const handleConfirm = async () => {
    if (!selectedDoctor || !startTime || !endTime) return;

    try {
      await axios.post("http://localhost:8080/api/appointments/book", {
        doctorId: selectedDoctor.id,
        patientName,
        contactNumber,
        clinicName: hospitals.find(h => h.id === parseInt(selectedHospital))?.name || "",
        startTime,
        endTime
      });

      setConfirmationMessage("✅ Appointment confirmed successfully!");
      setErrorMessage("");
      setPatientName("");
      setContactNumber("");
      setStartTime("");
      setEndTime("");
      setSelectedDoctor(null);
    } catch (err) {
      if (err.response?.data?.message?.includes("already booked")) {
        setErrorMessage("❌ The appointment time is already booked.");
      } else {
        setErrorMessage("❌ Error booking appointment.");
      }
      setConfirmationMessage("");
    }
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-2">AMSHC</h1>
      <TopNav />

      <div className="max-w-6xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg mt-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
          {/* City and Hospital */}
          <div>
            <h2 className="text-xl font-semibold mb-2">Select City & Clinic</h2>
            <select
              className="w-full p-3 rounded-md text-black mb-4"
              value={selectedCity}
              onChange={(e) => {
                setSelectedCity(e.target.value);
                setSelectedHospital("");
                setSelectedDoctor(null);
              }}
            >
              <option value="">-- Choose City --</option>
              {cities.map((city) => (
                <option key={city.id} value={city.id}>
                  {city.name}
                </option>
              ))}
            </select>

            {hospitals.length > 0 && (
              <select
                className="w-full p-3 rounded-md text-black mb-4"
                value={selectedHospital}
                onChange={(e) => {
                  const hospitalId = e.target.value;
                  setSelectedHospital(hospitalId);
                  setSelectedDoctor(null);
                }}
              >
                <option value="">-- Choose Hospital --</option>
                {hospitals.map((h) => (
                  <option key={h.id} value={h.id}>
                    {h.name}
                  </option>
                ))}
              </select>
            )}

            {selectedHospital && (
              <div className="mt-4">
                <p className="text-lg font-semibold">
                  {hospitals.find(h => h.id === parseInt(selectedHospital))?.name}
                </p>
                <p className="text-sm text-gray-200">
                  {hospitals.find(h => h.id === parseInt(selectedHospital))?.address}
                </p>
                <a
                  href={`https://www.google.com/search?q=${encodeURIComponent(
                    hospitals.find(h => h.id === parseInt(selectedHospital))?.name || ""
                  )}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-300 underline"
                >
                  🔗 View on Google
                </a>
              </div>
            )}
          </div>

          {/* Doctor Selection */}
          {doctors.length > 0 && (
            <div>
              <h2 className="text-xl font-semibold mb-3">Choose a Doctor</h2>
              <div className="space-y-3">
                {doctors.map((doctor, index) => (
                  <div
                    key={index}
                    onClick={() => setSelectedDoctor(doctor)}
                    className={`p-4 rounded-md shadow-md cursor-pointer transition border bg-blue-700 ${
                      selectedDoctor?.id === doctor.id ? "border-white" : "border-transparent"
                    }`}
                  >
                    <div className="flex items-center gap-4">
                      <img
                        src={
                          doctor.gender === "Female"
                            ? "/doctor_images/female_doctor.png"
                            : "/doctor_images/male_doctor.png"
                        }
                        alt={doctor.name}
                        className="w-16 h-16 rounded-full object-cover"
                      />
                      <div>
                        <h3 className="text-lg font-semibold">{doctor.name}</h3>
                        <p className="text-sm text-gray-200">{doctor.specialization}</p>
                        <p className="text-xs text-gray-300">{doctor.experienceYears} years experience</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Appointment Form */}
        <div className="grid md:grid-cols-2 gap-4 mb-4">
          <input
            type="text"
            placeholder="Patient Name"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="datetime-local"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
            className="w-full p-3 rounded-md text-black col-span-2"
          />
          <input
            type="datetime-local"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
            className="w-full p-3 rounded-md text-black col-span-2"
          />
        </div>

        {/* Confirm */}
        {selectedDoctor && patientName && contactNumber && startTime && endTime && (
          <div className="text-center">
            <button
              onClick={handleConfirm}
              className="px-8 py-3 bg-green-500 hover:bg-green-400 text-white rounded-lg font-semibold"
            >
              Confirm Appointment
            </button>
            {confirmationMessage && (
              <p className="mt-4 text-green-300 font-medium">
                {confirmationMessage}
              </p>
            )}
            {errorMessage && (
              <p className="mt-4 text-red-300 font-medium">{errorMessage}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Booking;
