import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const DoctorProfile = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { doctor } = state || {};

  if (!doctor) return <p>No doctor selected</p>;

  return (
    <div className="p-6">
      <h2 className="text-2xl mb-4">Doctor Profile</h2>
      <p><strong>Name:</strong> {doctor.name}</p>
      <p><strong>Specialty:</strong> {doctor.type}</p>
      <p><strong>Experience:</strong> {doctor.history}</p>

      <button
        className="mt-4 bg-blue-600 text-white px-4 py-2 rounded"
        onClick={() => navigate('/book-appointment', { state: { doctor } })}
      >
        Book Appointment
      </button>
    </div>
  );
};

export default DoctorProfile;
