import React, { useEffect, useState } from 'react';
import axios from 'axios';
import TopNav from '../components/TopNav';

const Doctor = () => {
  const [doctors, setDoctors] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    specialization: '',
    history: '',
    clinic: '',
    gender: 'male'
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editingDoctorId, setEditingDoctorId] = useState(null);

  useEffect(() => {
    fetchDoctors();
  }, []);

  const fetchDoctors = async () => {
    const response = await axios.get('/api/doctors');
    setDoctors(response.data);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isEditing) {
      await axios.put(`/api/doctors/${editingDoctorId}`, formData);
      setIsEditing(false);
      setEditingDoctorId(null);
    } else {
      await axios.post('/api/doctors/add', formData);
    }
    setFormData({ name: '', specialization: '', history: '', clinic: '', gender: 'male' });
    fetchDoctors();
  };

  const handleEdit = (doctor) => {
    setFormData({
      name: doctor.name,
      specialization: doctor.specialization,
      history: doctor.history,
      clinic: doctor.clinic,
      gender: doctor.gender || 'male'
    });
    setIsEditing(true);
    setEditingDoctorId(doctor.id);
  };

  const handleDelete = async (id) => {
    await axios.delete(`/api/doctors/${id}`);
    fetchDoctors();
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <TopNav />
      <div className="max-w-4xl mx-auto bg-blue-800 p-6 rounded-xl shadow-lg">
        <h2 className="text-2xl font-bold mb-4 text-center">Doctor Management</h2>

        {/* Add/Edit Doctor Form */}
        <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4 mb-8">
          <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Doctor Name" className="p-3 text-black rounded-md" required />
          <input type="text" name="specialization" value={formData.specialization} onChange={handleChange} placeholder="Specialization" className="p-3 text-black rounded-md" required />
          <input type="text" name="history" value={formData.history} onChange={handleChange} placeholder="Experience / History" className="p-3 text-black rounded-md" required />
          <input type="text" name="clinic" value={formData.clinic} onChange={handleChange} placeholder="Clinic / Hospital Name" className="p-3 text-black rounded-md" required />
          <select name="gender" value={formData.gender} onChange={handleChange} className="p-3 text-black rounded-md">
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
          <button type="submit" className="bg-green-500 hover:bg-green-400 text-white px-4 py-2 rounded-md">
            {isEditing ? 'Update Doctor' : 'Add Doctor'}
          </button>
        </form>

        {/* Doctor List */}
        <h3 className="text-xl font-semibold mb-2">Current Doctors</h3>
        <div className="space-y-4">
          {doctors.map((doc) => (
            <div key={doc.id} className="bg-blue-700 p-4 rounded-md flex justify-between items-start">
              <div>
                <p className="font-bold text-lg">{doc.name} ({doc.gender})</p>
                <p>{doc.specialization} | {doc.history}</p>
                <p className="text-sm text-blue-200">Clinic: {doc.clinic}</p>
              </div>
              <div className="space-x-2">
                <button onClick={() => handleEdit(doc)} className="bg-yellow-400 hover:bg-yellow-300 text-black px-3 py-1 rounded">Edit</button>
                <button onClick={() => handleDelete(doc.id)} className="bg-red-500 hover:bg-red-400 text-white px-3 py-1 rounded">Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Doctor;
