// ✅ src/pages/Dashboard.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const citiesWithHospitals = {
  "Quezon City": [
    { name: "St. Luke's Medical Center", address: "279 E Rodriguez Sr. Ave" },
    { name: "East Avenue Medical Center", address: "East Ave, Diliman" },
    { name: "Capitol Medical Center", address: "Matalino St, Diliman" },
  ],
  "Manila": [
    { name: "Manila Doctors Hospital", address: "Kalaw Ave, Ermita" },
    { name: "Philippine General Hospital", address: "Taft Ave" },
    { name: "Ospital ng Maynila", address: "Malate, Manila" },
  ],
  "Davao City": [
    { name: "Davao Doctors Hospital", address: "Quirino Ave" },
    { name: "Southern Philippines Medical Center", address: "JP Laurel Ave" },
    { name: "San Pedro Hospital", address: "C. de Guzman St" },
  ],
  "Cebu City": [
    { name: "Cebu Doctors' University Hospital", address: "Osmeña Blvd" },
    { name: "Chong Hua Hospital", address: "Fuente Osmeña" },
    { name: "Perpetual Succour Hospital", address: "Gorordo Ave" },
  ],
  "Zamboanga City": [
    { name: "Zamboanga Medical Center", address: "Santa Maria" },
    { name: "Brent Hospital", address: "R.T. Lim Blvd" },
    { name: "Zamboanga Doctor's Hospital", address: "Veterans Ave" },
  ],
  "Antipolo": [
    { name: "Antipolo Doctors Hospital", address: "Circumferential Rd" },
    { name: "Unciano Medical Center", address: "Sumulong Hwy" },
    { name: "Amang Rodriguez Hospital", address: "Sitio Tanza" },
  ],
  "Taguig": [
    { name: "St. Luke’s BGC", address: "Bonifacio Global City" },
    { name: "Taguig-Pateros Hospital", address: "Brgy. Ususan" },
    { name: "Medical Center Taguig", address: "Brgy. Tuktukan" },
  ],
  "Pasig": [
    { name: "The Medical City", address: "Ortigas Ave" },
    { name: "Pasig City General Hospital", address: "Caruncho Ave" },
    { name: "Mission Hospital", address: "Pinagbuhatan" },
  ],
  "Cagayan de Oro": [
    { name: "Northern Mindanao Medical Center", address: "Capitol Compound" },
    { name: "Polymedic Medical Plaza", address: "Kauswagan Highway" },
    { name: "Capitol University Medical Center", address: "Corrales Ext" },
  ],
  "Dasmariñas": [
    { name: "De La Salle University Medical Center", address: "Gov. D. Mangubat Ave" },
    { name: "Asia Medic Family Hospital", address: "Salitran" },
    { name: "Emilio Aguinaldo College Medical Center", address: "Paliparan" },
  ],
};

const Dashboard = () => {
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedHospital, setSelectedHospital] = useState(null);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const navigate = useNavigate();

  const doctors = [
    { id: 1, name: "Dr. Juan Dela Cruz", specialization: "Cardiologist", history: "10 years experience" },
    { id: 2, name: "Dr. Maria Santos", specialization: "Dentist", history: "5 years experience" },
    { id: 3, name: "Dr. Jose Rizal", specialization: "Pediatrician", history: "8 years experience" },
  ];

  const handleBooking = () => {
    if (selectedCity && selectedHospital && selectedDoctor) {
      navigate("/booking", {
        state: {
          city: selectedCity,
          hospital: selectedHospital.name,
          doctorId: selectedDoctor.id,
          doctorName: selectedDoctor.name,
        },
      });
    }
  };

  return (
    <div className="min-h-screen p-6 bg-blue-50">
      <div className="max-w-4xl mx-auto bg-white rounded shadow p-6">
        <h1 className="text-2xl font-bold mb-4">Select Location</h1>
        <select
          className="w-full border px-4 py-2 rounded mb-4"
          value={selectedCity}
          onChange={(e) => {
            setSelectedCity(e.target.value);
            setSelectedHospital(null);
            setSelectedDoctor(null);
          }}
        >
          <option value="">-- Select a City --</option>
          {Object.keys(citiesWithHospitals).map((city) => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>

        {selectedCity && (
          <>
            <h2 className="text-xl font-semibold mb-2">Choose a Hospital</h2>
            <ul className="mb-4">
              {citiesWithHospitals[selectedCity].map((hospital, idx) => (
                <li
                  key={idx}
                  className={`cursor-pointer border p-3 rounded mb-2 ${selectedHospital === hospital ? "bg-blue-100" : "hover:bg-blue-50"}`}
                  onClick={() => setSelectedHospital(hospital)}
                >
                  <strong>{hospital.name}</strong>
                  <p className="text-sm text-gray-600">{hospital.address}</p>
                </li>
              ))}
            </ul>
          </>
        )}

        {selectedHospital && (
          <>
            <h2 className="text-xl font-semibold mb-2">Select Doctor</h2>
            <ul>
              {doctors.map((doc) => (
                <li
                  key={doc.id}
                  className={`cursor-pointer border p-3 rounded mb-2 ${selectedDoctor?.id === doc.id ? "bg-blue-100" : "hover:bg-blue-50"}`}
                  onClick={() => setSelectedDoctor(doc)}
                >
                  <strong>{doc.name}</strong> - {doc.specialization}
                  <p className="text-sm text-gray-600">{doc.history}</p>
                </li>
              ))}
            </ul>

            <button
              className="mt-4 bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
              onClick={handleBooking}
            >
              Book Appointment
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
