import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Register = () => {
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [fullName, setFullName] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/api/auth/register", {
        email,
        password,
        username,
        fullName,
        role: "PATIENT", // default role
      });

      setMessage("✅ Registration successful!");
      console.log(response.data);
      setTimeout(() => navigate("/login"), 1500);
    } catch (error) {
      console.error("Registration error:", error.response?.data || error.message);
      setMessage("❌ Registration failed. Please check your inputs.");
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-blue-900">
      <div className="bg-gradient-to-br from-blue-700 to-blue-500 p-10 rounded-lg shadow-lg w-full max-w-md text-center text-white">
        <h1 className="text-3xl font-bold mb-4">AMSHC</h1>
        <h2 className="text-xl font-semibold mb-6">Create Account</h2>
        <form onSubmit={handleRegister} className="space-y-4">
          <input
            type="text"
            placeholder="Full Name"
            className="w-full px-4 py-3 rounded-full text-black"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Username"
            className="w-full px-4 py-3 rounded-full text-black"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="email"
            placeholder="Email"
            className="w-full px-4 py-3 rounded-full text-black"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full px-4 py-3 rounded-full text-black"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button
            type="submit"
            className="w-full bg-white text-blue-700 font-semibold py-3 rounded-full hover:bg-blue-100 transition"
          >
            Register
          </button>
        </form>
        <p className="mt-4 text-sm">{message}</p>
        <div className="mt-4">
          <a href="/login" className="block text-sm underline">
            Back to login
          </a>
        </div>
      </div>
    </div>
  );
};

export default Register;
