const Dashboard = () => {
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [confirmationMessage, setConfirmationMessage] = useState("");

  const handleConfirm = () => {
    const appointment = {
      city: selectedCity,
      clinic: selectedClinic?.name,
      doctor: selectedDoctor,
      patientName,
      contactNumber
    };
    console.log("ðŸ“‹ Appointment Booked:", appointment);
    setConfirmationMessage("âœ… Appointment confirmed successfully!");
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <div className="text-center text-3xl font-bold mb-8">AMSHC</div>

      <div className="max-w-6xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
          {/* City & Clinic Selection */}
          <div>
            <h2 className="text-xl font-semibold mb-2">Select City & Clinic</h2>
            <select
              className="w-full p-3 rounded-md text-black mb-4"
              value={selectedCity}
              onChange={(e) => {
                setSelectedCity(e.target.value);
                setSelectedClinic(null);
                setSelectedDoctor(null);
              }}
            >
              <option value="">-- Choose City --</option>
              {topCities.map((city) => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>

            {selectedCity && (
              <div className="space-y-3">
                {clinicsPerCity[selectedCity]?.map((clinic, index) => (
                  <button
                    key={index}
                    onClick={() => {
                      setSelectedClinic(clinic);
                      setSelectedDoctor(null);
                    }}
                    className={`w-full px-4 py-2 rounded-md transition text-left ${
                      selectedClinic?.name === clinic.name
                        ? "bg-white text-blue-900 font-bold"
                        : "bg-blue-500 hover:bg-blue-400"
                    }`}
                  >
                    {clinic.name}
                  </button>
                ))}
              </div>
            )}

            {selectedClinic && (
              <div className="mt-6">
                <p className="text-lg font-semibold">{selectedClinic.name}</p>
                <p className="text-sm text-gray-200 mb-2">{selectedClinic.address}</p>
                <a
                  href={`https://www.google.com/search?q=${encodeURIComponent(selectedClinic.name)}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-300 underline"
                >
                  ðŸ”— View on Google
                </a>
              </div>
            )}
          </div>

          {/* Doctor Selection */}
          {selectedClinic && (
            <div>
              <h2 className="text-xl font-semibold mb-3">Choose a Doctor</h2>
              <div className="space-y-3">
                {["Dr. Maria Reyes", "Dr. John Cruz", "Dr. Anna Santos"].map((doctorName, index) => (
                  <div
                    key={index}
                    onClick={() =>
                      setSelectedDoctor({
                        name: doctorName,
                        specialization: index === 0 ? "Cardiologist" : index === 1 ? "Dentist" : "Pediatrician",
                        history: `${8 + index} years experience`
                      })
                    }
                    className={`p-4 rounded-md shadow-md cursor-pointer transition border ${
                      selectedDoctor?.name === doctorName ? "border-white" : "border-transparent"
                    }`}
                  >
                    <h3 className="text-lg font-semibold">{doctorName}</h3>
                    <p className="text-sm text-gray-200">
                      {index === 0 ? "Cardiologist" : index === 1 ? "Dentist" : "Pediatrician"}
                    </p>
                    <p className="text-xs text-gray-300">{8 + index} years experience</p>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Patient Info */}
        <div className="grid md:grid-cols-2 gap-4 mb-6">
          <input
            type="text"
            placeholder="Patient Name"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
        </div>

        {/* Confirm Button */}
        {selectedDoctor && patientName && contactNumber && (
          <div className="text-center">
            <button
              onClick={handleConfirm}
              className="px-8 py-3 bg-green-500 hover:bg-green-400 text-white rounded-lg font-semibold"
            >
              Confirm Appointment
            </button>
            {confirmationMessage && (
              <p className="mt-4 text-green-300 font-medium">{confirmationMessage}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
