import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  CalendarDays,
  User,
  ClipboardList,
  History,
  Home,
  LogOut,
} from 'lucide-react';

const TopNav = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const hideOnPaths = ['/login', '/register'];

  if (hideOnPaths.includes(location.pathname)) return null;

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div className="bg-blue-800 text-white py-4 px-6">
      <div className="text-center font-bold text-lg mb-2">Appointment</div>

      <div className="flex flex-wrap items-center justify-between max-w-6xl mx-auto">
        {/* Left Side Icons including Home */}
        <div className="flex flex-wrap gap-8 items-center">
          <button onClick={() => navigate('/dashboard')} className="flex flex-col items-center hover:text-blue-300">
            <Home size={28} />
            <span className="text-sm mt-1">Home</span>
          </button>

          <button onClick={() => navigate('/booking')} className="flex flex-col items-center hover:text-blue-300">
            <CalendarDays size={28} />
            <span className="text-sm mt-1">Book now</span>
          </button>

          <button onClick={() => navigate('/choose-doctor')} className="flex flex-col items-center hover:text-blue-300">
            <User size={28} />
            <span className="text-sm mt-1">Choose Doctor</span>
          </button>

          <button onClick={() => navigate('/appointment-date')} className="flex flex-col items-center hover:text-blue-300">
            <ClipboardList size={28} />
            <span className="text-sm mt-1">Date Appt.</span>
          </button>

          <button onClick={() => navigate('/booking-history')} className="flex flex-col items-center hover:text-blue-300">
            <History size={28} />
            <span className="text-sm mt-1">Booking History</span>
          </button>
        </div>

        {/* Logout Icon */}
        <div className="ml-6">
          <button onClick={handleLogout} className="flex flex-col items-center hover:text-red-400">
            <LogOut size={28} />
            <span className="text-sm mt-1">Logout</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default TopNav;
