import React, { useState, useEffect } from "react";
import axios from "axios";
import TopNav from "../components/TopNav";

const Booking = () => {
  const [cities, setCities] = useState([]);
  const [hospitals, setHospitals] = useState([]);
  const [selectedCityId, setSelectedCityId] = useState("");
  const [selectedCityName, setSelectedCityName] = useState("");
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [confirmationMessage, setConfirmationMessage] = useState("");

  // Fetch cities on mount
  useEffect(() => {
    axios.get("http://localhost:8080/api/cities")
      .then((res) => setCities(res.data))
      .catch((err) => console.error("âŒ Error fetching cities:", err));
  }, []);

  // Fetch hospitals when a city is selected
  const handleCityChange = (e) => {
    const cityId = e.target.value;
    const city = cities.find((c) => c.id.toString() === cityId);

    setSelectedCityId(cityId);
    setSelectedCityName(city?.name || "");
    setSelectedClinic(null);
    setSelectedDoctor(null);
    setHospitals([]);

    if (cityId) {
      axios.get(`http://localhost:8080/api/hospitals/city/${cityId}`)
        .then((res) => setHospitals(res.data))
        .catch((err) => console.error("âŒ Error fetching hospitals:", err));
    }
  };

  const handleConfirm = () => {
    const appointment = {
      city: selectedCityName,
      clinic: selectedClinic?.name,
      doctor: selectedDoctor,
      patientName,
      contactNumber
    };
    console.log("ðŸ“‹ Appointment Booked:", appointment);
    setConfirmationMessage("âœ… Appointment confirmed successfully!");
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-2">AMSHC</h1>
      <TopNav />

      <div className="max-w-6xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg mt-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
          {/* City & Clinic Selection */}
          <div>
            <h2 className="text-xl font-semibold mb-2">Select City & Clinic</h2>
            <select
              className="w-full p-3 rounded-md text-black mb-4"
              value={selectedCityId}
              onChange={handleCityChange}
            >
              <option value="">-- Choose City --</option>
              {cities.map((city) => (
                <option key={city.id} value={city.id}>{city.name}</option>
              ))}
            </select>

            {hospitals.length > 0 && (
              <div className="space-y-3">
                {hospitals.map((clinic) => (
                  <button
                    key={clinic.id}
                    onClick={() => {
                      setSelectedClinic(clinic);
                      setSelectedDoctor(null);
                    }}
                    className={`w-full px-4 py-2 rounded-md transition text-left ${
                      selectedClinic?.id === clinic.id
                        ? "bg-white text-blue-900 font-bold"
                        : "bg-blue-500 hover:bg-blue-400"
                    }`}
                  >
                    {clinic.name}
                  </button>
                ))}
              </div>
            )}

            {selectedClinic && (
              <div className="mt-6">
                <p className="text-lg font-semibold">{selectedClinic.name}</p>
                <p className="text-sm text-gray-200 mb-2">{selectedClinic.address}</p>
                <a
                  href={`https://www.google.com/search?q=${encodeURIComponent(selectedClinic.name)}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-300 underline"
                >
                  ðŸ”— View on Google
                </a>
              </div>
            )}
          </div>

          {/* Placeholder for Doctor Selection (coming soon) */}
          {selectedClinic && (
            <div>
              <h2 className="text-xl font-semibold mb-3">Choose a Doctor (Coming Next)</h2>
              <p className="text-gray-300">Doctor integration will be implemented next.</p>
            </div>
          )}
        </div>

        {/* Patient Info */}
        <div className="grid md:grid-cols-2 gap-4 mb-6">
          <input
            type="text"
            placeholder="Patient Name"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
        </div>

        {/* Confirm Button */}
        {selectedClinic && patientName && contactNumber && (
          <div className="text-center">
            <button
              onClick={handleConfirm}
              className="px-8 py-3 bg-green-500 hover:bg-green-400 text-white rounded-lg font-semibold"
            >
              Confirm Appointment
            </button>
            {confirmationMessage && (
              <p className="mt-4 text-green-300 font-medium">{confirmationMessage}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Booking;
