import React, { useState } from "react";

const topCities = [
  "Quezon City", "Manila", "Davao City", "Caloocan", "Cebu City",
  "Zamboanga City", "Taguig", "Antipolo", "Pasig", "Cagayan de Oro"
];

const clinicsPerCity = {
  "Quezon City": ["Covid Clinic", "Govt. Hospital", "MemorialCare"],
  "Manila": ["ManilaMed", "PGH", "Manila Doctors"],
};

const doctorsPerClinic = {
  "Covid Clinic": [
    { name: "Dr. Juan Dela Cruz", specialization: "Cardiologist", history: "10+ years experience" },
    { name: "Dr. Maria Santos", specialization: "Pediatrician", history: "5+ years experience" }
  ],
  "Govt. Hospital": [
    { name: "Dr. Jose Rizal", specialization: "Dermatologist", history: "7+ years experience" }
  ],
};

const patients = [
  { id: 1, label: "My Self", image: "https://i.imgur.com/1XK0P9y.png" },
  { id: 2, label: "My child", image: "https://i.imgur.com/nz7kXek.png" },
  { id: 3, label: "My wife", image: "https://i.imgur.com/yPj3sZL.png" },
];

const Dashboard = () => {
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedClinic, setSelectedClinic] = useState("");
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");

  return (
    <div className="min-h-screen bg-blue-900 text-white font-sans p-6">
      <div className="text-center text-3xl font-bold mb-8">AMSHC</div>

      <div className="max-w-6xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg">
        {/* Location & Clinic Selection */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
          <div>
            <h2 className="text-xl font-semibold mb-2">Where would you like to meet?</h2>
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/8/88/Google_Maps_sample.png"
              alt="Map"
              className="rounded-md mb-4 border"
            />
            <select
              className="w-full p-3 rounded-md text-black mb-3"
              value={selectedCity}
              onChange={(e) => {
                setSelectedCity(e.target.value);
                setSelectedClinic("");
                setSelectedDoctor(null);
              }}
            >
              <option value="">-- Choose City --</option>
              {topCities.map((city) => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>

            {selectedCity && (
              <div className="space-y-2">
                {clinicsPerCity[selectedCity]?.map((clinic, index) => (
                  <button
                    key={index}
                    onClick={() => {
                      setSelectedClinic(clinic);
                      setSelectedDoctor(null);
                    }}
                    className={`w-full px-4 py-2 rounded-md transition text-left ${
                      selectedClinic === clinic
                        ? "bg-white text-blue-900 font-bold"
                        : "bg-blue-500 hover:bg-blue-400"
                    }`}
                  >
                    {clinic}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Doctor Selection */}
          {selectedClinic && (
            <div>
              <h2 className="text-xl font-semibold mb-2">Choose a Doctor</h2>
              <div className="space-y-3">
                {doctorsPerClinic[selectedClinic]?.map((doctor, index) => (
                  <div
                    key={index}
                    onClick={() => setSelectedDoctor(doctor)}
                    className={`p-4 rounded-md shadow-md cursor-pointer transition border ${
                      selectedDoctor?.name === doctor.name ? "border-white" : "border-transparent"
                    }`}
                  >
                    <h3 className="text-lg font-semibold">{doctor.name}</h3>
                    <p className="text-sm text-gray-200">{doctor.specialization}</p>
                    <p className="text-xs text-gray-300">{doctor.history}</p>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Patient Selection */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-2">Who is this patient?</h2>
          <div className="flex items-center space-x-6 overflow-x-auto">
            <div className="flex flex-col items-center">
              <div className="w-16 h-16 bg-blue-400 rounded-full flex items-center justify-center text-xl font-bold cursor-pointer hover:bg-blue-300">
                +
              </div>
              <span className="mt-1 text-sm">Add</span>
            </div>
            {patients.map((p) => (
              <div
                key={p.id}
                onClick={() => setSelectedPatient(p)}
                className={`flex flex-col items-center cursor-pointer ${
                  selectedPatient?.id === p.id ? "ring-4 ring-white rounded-full" : ""
                }`}
              >
                <img src={p.image} alt={p.label} className="w-16 h-16 rounded-full object-cover" />
                <span className="mt-1 text-sm">{p.label}</span>
              </div>
            ))}
          </div>
        </div>

        {/* Appointment Info */}
        <div className="grid md:grid-cols-2 gap-4 mb-6">
          <input
            type="text"
            placeholder="Patient Name"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
        </div>

        {/* Confirm Button */}
        {selectedDoctor && selectedPatient && patientName && contactNumber && (
          <div className="text-center">
            <button className="px-8 py-3 bg-green-500 hover:bg-green-400 text-white rounded-lg font-semibold">
              Confirm Appointment
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
