import React from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();

  const handleBookingClick = () => {
    navigate("/book");
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-900 to-blue-700 text-white flex flex-col items-center justify-center p-8">
      <h1 className="text-4xl font-bold mb-6 text-center">Welcome to AMSHC</h1>
      <p className="text-lg text-gray-200 mb-10 text-center max-w-xl">
        Book your appointment easily at clinics and hospitals near you. Skip the hassle and connect with doctors quickly and conveniently.
      </p>

      <div className="w-full max-w-md bg-blue-800 rounded-xl shadow-lg p-6 text-center">
        <h2 className="text-2xl font-semibold mb-4">Start Here</h2>
        <button
          onClick={handleBookingClick}
          className="w-full bg-white text-blue-900 font-bold py-3 px-6 rounded-lg transition hover:bg-blue-100"
        >
          ðŸ“… Book Appointment
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
