import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const Register = () => {
  const [fullName, setFullName] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8080/api/auth/register", {
        fullName,
        username,
        email,
        password,
        role: "PATIENT",
      });

      console.log(response.data);
      setMessage("Registration successful! Redirecting to login...");
      setTimeout(() => navigate("/login"), 1500);
    } catch (error) {
      console.error("Registration error:", error.response?.data || error.message);
      setMessage("Registration failed. Please try again.");
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-blue-900">
      <div className="bg-gradient-to-br from-blue-700 to-blue-500 p-10 rounded-lg shadow-lg w-full max-w-md text-center text-white">
        <h1 className="text-3xl font-bold mb-2">AMSHC</h1>
        <h2 className="text-xl font-semibold mb-6">Create Account</h2>
        <form onSubmit={handleRegister} className="space-y-4">
          <input
            type="text"
            placeholder="Full Name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            className="w-full px-4 py-3 rounded-full text-black"
            required
          />
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full px-4 py-3 rounded-full text-black"
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-4 py-3 rounded-full text-black"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-4 py-3 rounded-full text-black"
            required
          />
          <button
            type="submit"
            className="w-full bg-white text-blue-700 font-semibold py-3 rounded-full hover:bg-blue-100 transition"
          >
            Register
          </button>
        </form>
        <p className="mt-4 text-sm">{message}</p>
        <Link to="/login" className="block text-sm underline mt-4">
          Back to login
        </Link>
      </div>
    </div>
  );
};

export default Register;
