import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  CalendarDays,
  User,
  ClipboardList,
  History,
  Home,
} from 'lucide-react';

const TopNav = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const hideOnPaths = ['/login', '/register'];

  if (hideOnPaths.includes(location.pathname)) return null;

  return (
    <div className="bg-blue-800 text-white py-4 px-6">
      <div className="text-center font-bold text-lg mb-2">Appointment</div>
      <div className="flex justify-between items-center max-w-4xl mx-auto">
        <button
          onClick={() => navigate('/booking')}
          className="flex flex-col items-center hover:text-blue-300"
        >
          <CalendarDays size={28} />
          <span className="text-sm mt-1">Book now</span>
        </button>

        <button
          onClick={() => navigate('/choose-doctor')}
          className="flex flex-col items-center hover:text-blue-300"
        >
          <User size={28} />
          <span className="text-sm mt-1">Choose Doctor</span>
        </button>

        <button
          onClick={() => navigate('/appointment-date')}
          className="flex flex-col items-center hover:text-blue-300"
        >
          <ClipboardList size={28} />
          <span className="text-sm mt-1">Date Appt.</span>
        </button>

        <button
          onClick={() => navigate('/booking-history')}
          className="flex flex-col items-center hover:text-blue-300"
        >
          <History size={28} />
          <span className="text-sm mt-1">Booking History</span>
        </button>

        <button
          onClick={() => navigate('/dashboard')}
          className="flex flex-col items-center hover:text-blue-300"
        >
          <Home size={28} />
          <span className="text-sm mt-1">Home</span>
        </button>
      </div>
    </div>
  );
};

export default TopNav;
