import React, { useState } from "react";

const Dashboard = () => {
  const [selectedClinic, setSelectedClinic] = useState("");
  const [selectedRelation, setSelectedRelation] = useState("");
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Booking appointment:", {
      clinic: selectedClinic,
      relation: selectedRelation,
      name: patientName,
      contact: contactNumber,
    });
    alert("Appointment submitted successfully!");
  };

  return (
    <div className="h-screen bg-blue-900 text-white flex flex-col items-center p-6">
      <header className="text-3xl font-bold mt-4">AMSHC</header>

      <div className="bg-blue-800 p-6 mt-6 rounded-xl w-full max-w-4xl shadow-lg">
        <h2 className="text-xl font-semibold mb-2">Where would you like to meet?</h2>
        <div className="grid md:grid-cols-2 gap-6">
          <img
            src="https://via.placeholder.com/300x180.png?text=Map"
            alt="Map"
            className="rounded-xl"
          />

          <div>
            <input
              type="text"
              placeholder="Try 'clinics near me'"
              className="w-full px-4 py-2 rounded-full text-black mb-4"
            />
            {[
              "Covid Clinic",
              "Govt. Hospital",
              "MemorialCare",
            ].map((clinic) => (
              <button
                key={clinic}
                onClick={() => setSelectedClinic(clinic)}
                className={`block w-full bg-white text-blue-800 font-semibold px-4 py-2 rounded-full mb-2 transition hover:bg-blue-100 ${
                  selectedClinic === clinic ? "ring-2 ring-white" : ""
                }`}
              >
                {clinic} - vellore,632007
              </button>
            ))}
          </div>
        </div>

        <div className="mt-8">
          <h3 className="text-lg font-semibold mb-2">Who is this patient?</h3>
          <div className="flex items-center gap-4">
            {["My Self", "My child", "My wife"].map((relation, index) => (
              <button
                key={index}
                onClick={() => setSelectedRelation(relation)}
                className={`p-4 bg-white text-blue-800 rounded-full font-medium transition ${
                  selectedRelation === relation ? "ring-2 ring-white" : ""
                }`}
              >
                {relation}
              </button>
            ))}
          </div>
        </div>

        <form onSubmit={handleSubmit} className="mt-8 space-y-4">
          <input
            type="text"
            placeholder="Patient Name"
            className="w-full px-4 py-3 rounded-full text-black"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            required
          />
          <input
            type="tel"
            placeholder="Contact Number"
            className="w-full px-4 py-3 rounded-full text-black"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            required
          />
          <button
            type="submit"
            className="w-full bg-white text-blue-800 font-semibold py-3 rounded-full hover:bg-blue-100 transition"
          >
            Book Appointment
          </button>
        </form>
      </div>
    </div>
  );
};

export default Dashboard;
