import React, { useState } from "react";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Email:", email);
    console.log("Password:", password);

    // üîê TODO: Later you'll send this to your backend for authentication
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#0B2C81] relative">
      <div className="absolute top-6 text-white text-2xl font-bold">AMSHC</div>

      <form
        onSubmit={handleSubmit}
        className="bg-gradient-to-r from-[#1138BD] to-[#1C3996] p-8 rounded-2xl shadow-xl w-full max-w-md flex flex-col gap-4 items-center"
      >
        <h2 className="text-white text-lg font-bold">login</h2>

        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-full rounded-full px-4 py-2 outline-none"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full rounded-full px-4 py-2 outline-none"
        />

        <button
          type="submit"
          className="w-full mt-4 bg-white text-[#0B2C81] font-semibold py-2 rounded-full hover:bg-gray-100"
        >
          Login
        </button>

        <div className="text-sm text-white mt-2 text-center">
          <p className="underline cursor-pointer">forgot password?</p>
          <p className="underline cursor-pointer">create an account</p>
        </div>
      </form>
    </div>
  );
};

export default Login;
