// src/pages/Doctors.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TopNav from '../components/TopNav';

const Doctors = () => {
  const [doctors, setDoctors] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    specialization: '',
    experience: '',
    gender: 'Male',
    clinic: ''
  });
  const [editingDoctor, setEditingDoctor] = useState(null);

  useEffect(() => {
    fetchDoctors();
  }, []);

  const fetchDoctors = async () => {
    try {
      const response = await axios.get('/api/doctors');
      setDoctors(response.data);
    } catch (error) {
      console.error('Failed to fetch doctors:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const doctorPayload = {
      ...formData,
      photo: formData.gender === 'Female'
        ? '/doctor_images/female_doctor.png'
        : '/doctor_images/male_doctor.png'
    };
    try {
      if (editingDoctor) {
        await axios.put(`/api/doctors/${editingDoctor.id}`, doctorPayload);
        setEditingDoctor(null);
      } else {
        await axios.post('/api/doctors/add', doctorPayload);
      }
      setFormData({ name: '', specialization: '', experience: '', gender: 'Male', clinic: '' });
      fetchDoctors();
    } catch (error) {
      console.error('Error submitting doctor:', error);
    }
  };

  const handleEdit = (doctor) => {
    setFormData(doctor);
    setEditingDoctor(doctor);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/doctors/${id}`);
      fetchDoctors();
    } catch (error) {
      console.error('Error deleting doctor:', error);
    }
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <TopNav />
      <h1 className="text-3xl font-bold text-center mb-6">Manage Doctors</h1>

      {/* Doctor Form */}
      <form onSubmit={handleSubmit} className="bg-blue-800 p-6 rounded-xl max-w-xl mx-auto mb-10 space-y-4">
        <input
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Doctor Name"
          className="w-full p-3 rounded-md text-black"
          required
        />
        <input
          name="specialization"
          value={formData.specialization}
          onChange={handleChange}
          placeholder="Specialization"
          className="w-full p-3 rounded-md text-black"
          required
        />
        <input
          name="experience"
          value={formData.experience}
          onChange={handleChange}
          placeholder="Experience (e.g., 10 years)"
          className="w-full p-3 rounded-md text-black"
          required
        />
        <select
          name="gender"
          value={formData.gender}
          onChange={handleChange}
          className="w-full p-3 rounded-md text-black"
        >
          <option>Male</option>
          <option>Female</option>
        </select>
        <input
          name="clinic"
          value={formData.clinic}
          onChange={handleChange}
          placeholder="Clinic Name (must match existing hospital)"
          className="w-full p-3 rounded-md text-black"
          required
        />
        <button type="submit" className="bg-green-500 w-full py-2 rounded font-bold text-white hover:bg-green-400">
          {editingDoctor ? 'Update Doctor' : 'Add Doctor'}
        </button>
      </form>

      {/* Doctor List */}
      <div className="max-w-5xl mx-auto grid gap-6 grid-cols-1 md:grid-cols-2">
        {doctors.map((doctor) => (
          <div key={doctor.id} className="bg-blue-700 p-4 rounded-lg shadow-md">
            <div className="flex items-center gap-4">
              <img src={doctor.photo} alt={doctor.name} className="w-16 h-16 rounded-full object-cover" />
              <div>
                <h3 className="text-lg font-bold">{doctor.name}</h3>
                <p>{doctor.specialization}</p>
                <p className="text-sm text-gray-300">{doctor.experience}</p>
                <p className="text-sm text-blue-300">Clinic: {doctor.clinic}</p>
              </div>
            </div>
            <div className="flex gap-4 mt-4">
              <button onClick={() => handleEdit(doctor)} className="bg-yellow-400 px-3 py-1 rounded text-black">Edit</button>
              <button onClick={() => handleDelete(doctor.id)} className="bg-red-500 px-3 py-1 rounded">Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Doctors;
