import React, { useState } from "react";

const clinics = [
  { name: "Covid Clinic", location: "vellore,632007" },
  { name: "Govt. Hospital", location: "vellore,632007" },
  { name: "MemorialCare", location: "vellore,632007" },
];

const patients = [
  { id: 1, label: "My Self", image: "https://i.imgur.com/1XK0P9y.png" },
  { id: 2, label: "My child", image: "https://i.imgur.com/nz7kXek.png" },
  { id: 3, label: "My wife", image: "https://i.imgur.com/yPj3sZL.png" },
];

const Dashboard = () => {
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");

  return (
    <div className="min-h-screen bg-blue-900 text-white font-sans p-6 relative">
      {/* Top Header */}
      <div className="text-center text-3xl font-bold mb-8">AMSHC</div>

      {/* Main Container */}
      <div className="max-w-5xl mx-auto bg-blue-800 rounded-xl p-8 shadow-lg flex flex-col lg:flex-row gap-8">
        {/* Left Section - Map */}
        <div className="flex-1">
          <h2 className="text-xl font-semibold mb-3">Where would you like to meet?</h2>
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/8/88/Google_Maps_sample.png"
            alt="Map"
            className="rounded-lg border mb-4"
          />
          <input
            type="text"
            placeholder='Try "clinics near me"'
            className="w-full p-2 rounded-md text-black mb-4"
          />
          <div className="space-y-2">
            {clinics.map((clinic, index) => (
              <button
                key={index}
                onClick={() => setSelectedClinic(clinic)}
                className={`w-full px-4 py-2 rounded-md transition ${
                  selectedClinic?.name === clinic.name
                    ? "bg-white text-blue-900 font-semibold"
                    : "bg-blue-500 hover:bg-blue-400"
                }`}
              >
                {clinic.name} <span className="text-sm font-light ml-2">{clinic.location}</span>
              </button>
            ))}
          </div>
        </div>

        {/* Right Section */}
        <div className="flex-1">
          {/* Patient Selector */}
          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-2">Who is this patient?</h2>
            <div className="flex space-x-4">
              <div className="flex flex-col items-center">
                <div className="w-16 h-16 bg-blue-400 rounded-full flex items-center justify-center text-xl font-bold cursor-pointer hover:bg-blue-300">
                  +
                </div>
                <span className="mt-2 text-sm">Add</span>
              </div>
              {patients.map((p) => (
                <div
                  key={p.id}
                  onClick={() => setSelectedPatient(p)}
                  className={`flex flex-col items-center cursor-pointer ${
                    selectedPatient?.id === p.id ? "ring-4 ring-white rounded-full" : ""
                  }`}
                >
                  <img src={p.image} alt={p.label} className="w-16 h-16 rounded-full object-cover" />
                  <span className="mt-2 text-sm">{p.label}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Appointment Form */}
          <div className="mb-4">
            <h2 className="text-xl font-semibold mb-2">Appointment For</h2>
            <input
              type="text"
              placeholder="Patient Name"
              value={patientName}
              onChange={(e) => setPatientName(e.target.value)}
              className="w-full p-2 mb-3 rounded-md text-black"
            />
            <input
              type="text"
              placeholder="Contact Number"
              value={contactNumber}
              onChange={(e) => setContactNumber(e.target.value)}
              className="w-full p-2 rounded-md text-black"
            />
          </div>

          {/* Confirm Button */}
          {selectedClinic && selectedPatient && patientName && contactNumber && (
            <button className="mt-4 w-full bg-green-500 hover:bg-green-400 text-white py-2 rounded-md font-semibold">
              Confirm Appointment
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
