import React, { useEffect, useState } from "react";
import axios from "axios";
import TopNav from "../components/TopNav";

const BookingHistory = () => {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const patientId = 1; // Replace with actual logged-in patient ID if stored in localStorage
        const response = await axios.get(`http://localhost:8080/api/appointments/patient/${patientId}`);
        setAppointments(response.data);
      } catch (error) {
        console.error("❌ Error fetching appointments", error);
      }
    };

    fetchAppointments();
  }, []);

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-6">Booking History</h1>
      <TopNav />

      <div className="max-w-4xl mx-auto bg-blue-800 rounded-lg p-6 mt-6 shadow-md">
        {appointments.length === 0 ? (
          <p className="text-center text-gray-300">No appointments found.</p>
        ) : (
          <ul className="space-y-4">
            {appointments.map((appt) => (
              <li key={appt.id} className="bg-blue-700 p-4 rounded shadow">
                <p><strong>Doctor ID:</strong> {appt.doctorId}</p>
                <p><strong>Date:</strong> {new Date(appt.appointmentTime).toLocaleString()}</p>
                <p><strong>Status:</strong> {appt.status}</p>
                <p><strong>Notes:</strong> {appt.notes}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default BookingHistory;
