import React, { useState } from "react";

const topCities = [
  "Quezon City", "Manila", "Davao City", "Caloocan", "Cebu City",
  "Zamboanga City", "Taguig", "Antipolo", "Pasig", "Cagayan de Oro"
];

const clinicsPerCity = {
  "Quezon City": ["St. Luke's", "East Avenue Med Center", "Quezon City General Hospital"],
  "Manila": ["PGH", "Manila Doctors", "ManilaMed"],
  "Davao City": ["Davao Medical Center", "San Pedro Hospital", "Brokenshire Hospital"],
  "Caloocan": ["Caloocan City Med Center", "Our Lady of Grace", "Martinez Hospital"],
  "Cebu City": ["Chong Hua", "Cebu Doctors University Hospital", "Vicente Sotto Hospital"],
  "Zamboanga City": ["Zamboanga Peninsula Hospital", "Brent Hospital", "Ciudad Medical Zamboanga"],
  "Taguig": ["St. Luke's BGC", "Taguig General Hospital", "Medical Center Taguig"],
  "Antipolo": ["Antipolo Doctors Hospital", "Unciano Medical Center", "Antipolo MedCare"],
  "Pasig": ["The Medical City", "Pasig City General Hospital", "Pasig Doctors Hospital"],
  "Cagayan de Oro": ["Northern Mindanao Medical Center", "Cagayan de Oro Polymedic", "Capitol University Medical Center"]
};

const doctorsPerClinic = {
  "St. Luke's": [
    { name: "Dr. Santos", specialization: "Cardiologist", history: "10+ years experience" },
    { name: "Dr. Dela Cruz", specialization: "Dentist", history: "7 years experience" },
    { name: "Dr. Reyes", specialization: "Pediatrician", history: "5 years experience" }
  ],
  "East Avenue Med Center": [
    { name: "Dr. Tan", specialization: "Dermatologist", history: "6 years experience" },
    { name: "Dr. Garcia", specialization: "Surgeon", history: "8 years experience" },
    { name: "Dr. Lee", specialization: "Neurologist", history: "4 years experience" }
  ],
  "Quezon City General Hospital": [
    { name: "Dr. Cruz", specialization: "Orthopedic", history: "9 years experience" },
    { name: "Dr. Ramos", specialization: "Gynecologist", history: "10 years experience" },
    { name: "Dr. Bautista", specialization: "ENT", history: "3 years experience" }
  ],
  "PGH": [
    { name: "Dr. Vega", specialization: "Cardiologist", history: "11 years experience" },
    { name: "Dr. Lopez", specialization: "Psychiatrist", history: "6 years experience" },
    { name: "Dr. Aquino", specialization: "Endocrinologist", history: "5 years experience" }
  ],
  // You can extend this pattern to all other clinics:
};

// For demo: Copy the doctors list to every other clinic not defined above
Object.values(clinicsPerCity).flat().forEach((clinic) => {
  if (!doctorsPerClinic[clinic]) {
    doctorsPerClinic[clinic] = [
      { name: `Dr. ${clinic} A`, specialization: "General Physician", history: "3+ years experience" },
      { name: `Dr. ${clinic} B`, specialization: "Cardiologist", history: "6+ years experience" },
      { name: `Dr. ${clinic} C`, specialization: "Dentist", history: "4+ years experience" }
    ];
  }
});

const Dashboard = () => {
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedClinic, setSelectedClinic] = useState("");
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");

  return (
    <div className="min-h-screen bg-blue-900 text-white font-sans p-6">
      <div className="text-center text-3xl font-bold mb-8">AMSHC</div>

      <div className="max-w-6xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg">
        {/* Location & Clinic */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
          <div>
            <h2 className="text-xl font-semibold mb-2">Where would you like to meet?</h2>
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/8/88/Google_Maps_sample.png"
              alt="Map"
              className="rounded-md mb-4 border"
            />
            <select
              className="w-full p-3 rounded-md text-black mb-3"
              value={selectedCity}
              onChange={(e) => {
                setSelectedCity(e.target.value);
                setSelectedClinic("");
                setSelectedDoctor(null);
              }}
            >
              <option value="">-- Choose City --</option>
              {topCities.map((city) => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>

            {selectedCity && (
              <div className="space-y-2">
                {clinicsPerCity[selectedCity]?.map((clinic, index) => (
                  <button
                    key={index}
                    onClick={() => {
                      setSelectedClinic(clinic);
                      setSelectedDoctor(null);
                    }}
                    className={`w-full px-4 py-2 rounded-md transition text-left ${
                      selectedClinic === clinic
                        ? "bg-white text-blue-900 font-bold"
                        : "bg-blue-500 hover:bg-blue-400"
                    }`}
                  >
                    {clinic}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Doctor Selection */}
          {selectedClinic && (
            <div>
              <h2 className="text-xl font-semibold mb-3">Choose a Doctor</h2>
              <div className="space-y-3">
                {doctorsPerClinic[selectedClinic]?.map((doctor, index) => (
                  <div
                    key={index}
                    onClick={() => setSelectedDoctor(doctor)}
                    className={`p-4 rounded-md shadow-md cursor-pointer transition border ${
                      selectedDoctor?.name === doctor.name ? "border-white" : "border-transparent"
                    }`}
                  >
                    <h3 className="text-lg font-semibold">{doctor.name}</h3>
                    <p className="text-sm text-gray-200">{doctor.specialization}</p>
                    <p className="text-xs text-gray-300">{doctor.history}</p>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Patient Info */}
        <div className="grid md:grid-cols-2 gap-4 mb-6">
          <input
            type="text"
            placeholder="Patient Name"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
        </div>

        {/* Confirm Button */}
        {selectedDoctor && patientName && contactNumber && (
          <div className="text-center">
            <button className="px-8 py-3 bg-green-500 hover:bg-green-400 text-white rounded-lg font-semibold">
              Confirm Appointment
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
