// ✅ src/pages/Login.js
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Import navigation
import { Link } from "react-router-dom";


const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/api/auth/login", {
        email,
        password,
      });

      setMessage("Login successful!");
      console.log("✅ Logged in:", response.data);

      setTimeout(() => {
        navigate("/dashboard"); // Redirect to dashboard after success
      }, 1000);
    } catch (error) {
      console.error("Login error:", error.response?.data || error.message);
      setMessage("Login failed. Please check your credentials.");
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-blue-900">
      <div className="bg-gradient-to-br from-blue-700 to-blue-500 p-10 rounded-lg shadow-lg w-full max-w-md text-center text-white">
        <h1 className="text-3xl font-bold mb-4">AMSHC</h1>
        <h2 className="text-xl font-semibold mb-6">login</h2>
        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            className="w-full px-4 py-3 rounded-full text-black"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full px-4 py-3 rounded-full text-black"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button
            type="submit"
            className="w-full bg-white text-blue-700 font-semibold py-3 rounded-full hover:bg-blue-100 transition"
          >
            Login
          </button>
        </form>
        <p className="mt-4 text-sm">{message}</p>
        <div className="mt-4">
          <a href="#" className="block text-sm underline">
            forgot password?
          </a>
          <Link to="/register" className="block text-sm underline">
              create an account
            </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
