import React, { useState } from "react";

const topCities = [
  "Quezon City", "Manila", "Davao City", "Caloocan", "Cebu City",
  "Zamboanga City", "Taguig", "Antipolo", "Pasig", "Cagayan de Oro"
];

const clinicsPerCity = {
  "Quezon City": ["QC General Hospital", "St. Luke’s Medical Center", "Fe Del Mundo Medical Center"],
  "Manila": ["ManilaMed", "Philippine General Hospital", "Manila Doctors Hospital"],
  // Add others if needed
};

const doctorsPerClinic = {
  "QC General Hospital": [
    { name: "Dr. Juan Dela Cruz", specialization: "Cardiologist", history: "10+ years experience" },
    { name: "Dr. Maria Santos", specialization: "Pediatrician", history: "5+ years experience" }
  ],
  "St. Luke’s Medical Center": [
    { name: "Dr. Jose Rizal", specialization: "Dermatologist", history: "7+ years experience" }
  ],
  // Add more as needed
};

const Dashboard = () => {
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedClinic, setSelectedClinic] = useState("");
  const [selectedDoctor, setSelectedDoctor] = useState(null);

  return (
    <div className="min-h-screen flex bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-md p-4">
        <h2 className="text-2xl font-bold mb-6">AMS Dashboard</h2>
        <ul className="space-y-4 text-gray-700">
          <li className="hover:text-blue-600 cursor-pointer">Home</li>
          <li className="hover:text-blue-600 cursor-pointer">Appointments</li>
          <li className="hover:text-blue-600 cursor-pointer">Messages</li>
          <li className="hover:text-blue-600 cursor-pointer">Profile</li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-8">
        <div className="mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">Welcome to Your Dashboard</h1>
          <p className="text-gray-500">Book an appointment in just a few steps</p>
        </div>

        {/* Step 1: Select City */}
        <div className="mb-4">
          <label className="block mb-2 text-gray-700 font-medium">Select a City</label>
          <select
            className="w-full p-3 border rounded-md"
            value={selectedCity}
            onChange={(e) => {
              setSelectedCity(e.target.value);
              setSelectedClinic("");
              setSelectedDoctor(null);
            }}
          >
            <option value="">-- Choose City --</option>
            {topCities.map((city) => (
              <option key={city} value={city}>{city}</option>
            ))}
          </select>
        </div>

        {/* Step 2: Select Clinic */}
        {selectedCity && (
          <div className="mb-4">
            <label className="block mb-2 text-gray-700 font-medium">Select a Clinic</label>
            <select
              className="w-full p-3 border rounded-md"
              value={selectedClinic}
              onChange={(e) => {
                setSelectedClinic(e.target.value);
                setSelectedDoctor(null);
              }}
            >
              <option value="">-- Choose Clinic --</option>
              {clinicsPerCity[selectedCity]?.map((clinic) => (
                <option key={clinic} value={clinic}>{clinic}</option>
              ))}
            </select>
          </div>
        )}

        {/* Step 3: Select Doctor */}
        {selectedClinic && (
          <div className="mb-6">
            <label className="block mb-2 text-gray-700 font-medium">Choose a Doctor</label>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              {doctorsPerClinic[selectedClinic]?.map((doctor, index) => (
                <div
                  key={index}
                  onClick={() => setSelectedDoctor(doctor)}
                  className={`p-4 border rounded-md shadow-sm cursor-pointer transition hover:shadow-lg ${
                    selectedDoctor?.name === doctor.name ? "border-blue-500" : ""
                  }`}
                >
                  <h3 className="text-lg font-semibold">{doctor.name}</h3>
                  <p className="text-sm text-gray-600">{doctor.specialization}</p>
                  <p className="text-sm text-gray-400">{doctor.history}</p>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Step 4: Confirm Booking */}
        {selectedDoctor && (
          <div className="p-4 bg-white border rounded-md shadow">
            <h3 className="text-xl font-semibold mb-2">Ready to Book?</h3>
            <p className="text-gray-700 mb-4">
              You're about to book an appointment with <strong>{selectedDoctor.name}</strong>, a{" "}
              {selectedDoctor.specialization} from {selectedClinic}, {selectedCity}.
            </p>
            <button className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition">
              Confirm Appointment
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
