import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const citiesWithHospitals = {
  Cebu: [
    { name: "Cebu Doctors' University Hospital", address: "Osmeña Blvd, Cebu City" },
    { name: "Chong Hua Hospital", address: "Don Mariano Cui St, Cebu City" },
    { name: "Vicente Sotto Memorial Medical Center", address: "B. Rodriguez St, Cebu City" },
  ],
  Manila: [
    { name: "Manila Doctors Hospital", address: "UN Avenue, Ermita" },
    { name: "Philippine General Hospital", address: "Taft Ave, Ermita" },
    { name: "ManilaMed", address: "United Nations Ave" },
  ],
  Davao: [
    { name: "Davao Doctors Hospital", address: "Quirino Ave, Davao City" },
    { name: "San Pedro Hospital", address: "Davao City" },
    { name: "Southern Philippines Medical Center", address: "Bajada, Davao City" },
  ],
  Quezon: [
    { name: "Quezon City General Hospital", address: "Project 8" },
    { name: "St. Luke's Medical Center", address: "E. Rodriguez Sr. Ave" },
    { name: "East Avenue Medical Center", address: "East Avenue" },
  ],
  Makati: [
    { name: "Makati Medical Center", address: "Legazpi Village" },
    { name: "Ospital ng Makati", address: "Pembo, Makati" },
    { name: "Centuria Medical Makati", address: "Century City" },
  ],
  Pasig: [
    { name: "The Medical City", address: "Ortigas Avenue" },
    { name: "Pasig City General Hospital", address: "Karangalan Village" },
    { name: "Tricity Medical Center", address: "Ortigas Center" },
  ],
  Bacolod: [
    { name: "Riverside Medical Center", address: "Bacolod City" },
    { name: "Bacolod Queen of Mercy Hospital", address: "Eroreco Subdivision" },
    { name: "South Bacolod General Hospital", address: "Tangub" },
  ],
  Iloilo: [
    { name: "St. Paul's Hospital Iloilo", address: "Gen. Luna St" },
    { name: "West Visayas State University Medical Center", address: "Iloilo City" },
    { name: "Iloilo Doctors' Hospital", address: "Ledesma St" },
  ],
  Zamboanga: [
    { name: "Zamboanga City Medical Center", address: "Zamboanga City" },
    { name: "Brent Hospital and Colleges", address: "Zamboanga City" },
    { name: "Ciudad Medical Zamboanga", address: "Zamboanga City" },
  ],
  Cagayan: [
    { name: "Polymedic Medical Plaza", address: "Cagayan de Oro" },
    { name: "Cagayan De Oro Medical Center", address: "Capistrano St" },
    { name: "Maria Reyna Hospital", address: "Cagayan de Oro" },
  ],
};

const Dashboard = () => {
  const [selectedCity, setSelectedCity] = useState(null);
  const navigate = useNavigate();

  const handleCityClick = (city) => {
    setSelectedCity(city);
    navigate("/hospitals", { state: { city, hospitals: citiesWithHospitals[city] } });
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-8">
      <h1 className="text-3xl font-bold text-center mb-8">AMSHC Dashboard</h1>
      <div className="max-w-4xl mx-auto">
        <h2 className="text-xl font-semibold mb-4 text-center">Select a City</h2>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {Object.keys(citiesWithHospitals).map((city) => (
            <button
              key={city}
              onClick={() => handleCityClick(city)}
              className="bg-white text-blue-700 px-6 py-3 rounded-lg shadow hover:bg-blue-100 transition"
            >
              {city}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
