// ✅ src/pages/BookingHistory.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import TopNav from "../components/TopNav";

const BookingHistory = () => {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    const role = localStorage.getItem("role");

    if (userId && role) {
      fetchAppointments(userId, role);
    }
  }, []);

  const fetchAppointments = async (id, role) => {
    try {
      const url =
        role === "doctor"
          ? `http://localhost:8080/api/appointments/doctor/${id}`
          : `http://localhost:8080/api/appointments/patient/${id}`;

      const res = await axios.get(url);
      setAppointments(res.data);
    } catch (err) {
      console.error("❌ Error fetching booking history", err);
    }
  };

  const formatDateTime = (dateTimeStr) => {
    return new Date(dateTimeStr).toLocaleString("en-US", {
      month: "long",
      day: "numeric",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit",
      hour12: true,
    });
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-2">AMSHC</h1>
      <TopNav />

      <div className="max-w-4xl mx-auto bg-blue-800 rounded-xl p-6 mt-6 shadow-lg">
        <h2 className="text-2xl font-semibold mb-6 text-center">Booking History</h2>

        {appointments.length === 0 ? (
          <p className="text-center text-gray-300">No appointments found.</p>
        ) : (
          <ul className="space-y-4">
            {appointments.map((appt) => (
              <li
                key={appt.id}
                className="bg-blue-700 p-4 rounded-lg shadow border border-blue-600"
              >
                <p><strong>Doctor ID:</strong> {appt.doctorId}</p>
                <p><strong>Status:</strong> {appt.status}</p>
                <p><strong>Start:</strong> {formatDateTime(appt.startTime)}</p>
                <p><strong>End:</strong> {formatDateTime(appt.endTime)}</p>
                <p><strong>Notes:</strong> {appt.notes}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default BookingHistory;
