import React, { useEffect, useState } from "react";
import axios from "axios";
import TopNav from "../components/TopNav";

const BookingHistory = () => {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const res = await axios.get("http://localhost:8080/api/appointments");
      setAppointments(res.data);
    } catch (error) {
      console.error("Failed to load booking history", error);
    }
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-2">AMSHC</h1>
      <TopNav />

      <div className="max-w-4xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg mt-6">
        <h2 className="text-2xl font-semibold mb-6 text-center">Booking History</h2>

        {appointments.length === 0 ? (
          <p className="text-center text-gray-300">No appointments found.</p>
        ) : (
          <div className="space-y-4">
            {appointments.map((appt) => (
              <div key={appt.id} className="bg-blue-700 p-4 rounded-lg shadow-md">
                <h3 className="text-lg font-bold">{appt.patientName}</h3>
                <p><strong>Doctor:</strong> {appt.doctor?.name || "N/A"} ({appt.doctor?.specialization || "N/A"})</p>
                <p><strong>Clinic:</strong> {appt.clinicName}</p>
                <p><strong>Contact:</strong> {appt.contactNumber}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default BookingHistory;
