// src/pages/Login.js
import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8080/api/auth/login", {
        email,
        password,
      });

      console.log("Login successful:", response.data);
      localStorage.setItem("token", response.data.token);
      alert("Login successful!");

      // TODO: redirect to dashboard if needed
    } catch (error) {
      console.error("Login failed:", error);
      alert("Invalid email or password");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#073B88]">
      <div className="bg-gradient-to-br from-blue-800 to-blue-600 rounded-xl p-10 shadow-xl w-full max-w-md">
        <h1 className="text-white text-3xl font-bold text-center mb-4">AMSHC</h1>
        <h2 className="text-white text-xl font-semibold text-center mb-6">login</h2>

        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            className="w-full px-5 py-3 rounded-full focus:outline-none text-gray-700"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full px-5 py-3 rounded-full focus:outline-none text-gray-700"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button
            type="submit"
            className="w-full bg-white text-[#073B88] font-semibold py-3 rounded-full hover:bg-gray-100 transition"
          >
            Login
          </button>
        </form>

        <div className="mt-4 text-center text-white">
          <p>
            <Link to="#" className="hover:underline">
              forgot password?
            </Link>
          </p>
          <p>
            <Link to="/signup" className="hover:underline">
              create an account
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
