import React, { useState, useEffect } from "react";
import TopNav from "../components/TopNav";
import axios from "axios";

const Booking = () => {
  const [cities, setCities] = useState([]);
  const [hospitals, setHospitals] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [patientName, setPatientName] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [confirmationMessage, setConfirmationMessage] = useState("");

  useEffect(() => {
    fetchCities();
  }, []);

  useEffect(() => {
    if (selectedCity) {
      fetchHospitals(selectedCity);
    }
  }, [selectedCity]);

  useEffect(() => {
    if (selectedClinic) {
      fetchDoctors(selectedClinic.id);
    }
  }, [selectedClinic]);

  const fetchCities = async () => {
    const res = await axios.get("http://localhost:8080/api/cities");
    setCities(res.data);
  };

  const fetchHospitals = async (cityName) => {
    const res = await axios.get("http://localhost:8080/api/hospitals");
    const filtered = res.data.filter(h => h.city.name === cityName);
    setHospitals(filtered);
    setSelectedClinic(null);
  };

  const fetchDoctors = async (hospitalId) => {
    const res = await axios.get(`http://localhost:8080/api/doctors/hospital/${hospitalId}`);
    setDoctors(res.data);
    setSelectedDoctor(null);
  };

  const handleConfirm = async () => {
    if (!selectedDoctor || !patientName || !contactNumber) return;

    const appointment = {
      patientName,
      contactNumber,
      doctorId: selectedDoctor.id,
      clinicName: selectedClinic.name
    };

    try {
      await axios.post("http://localhost:8080/api/appointments/book", appointment);
      setConfirmationMessage("âœ… Appointment confirmed successfully!");

      // Reset form
      setSelectedCity("");
      setSelectedClinic(null);
      setSelectedDoctor(null);
      setPatientName("");
      setContactNumber("");

      // Optionally clear confirmation message after 3 seconds
      setTimeout(() => setConfirmationMessage(""), 3000);
    } catch (err) {
      console.error("Booking failed", err);
    }
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-2">AMSHC</h1>
      <TopNav />

      <div className="max-w-6xl mx-auto bg-blue-800 rounded-xl p-6 md:p-10 shadow-lg mt-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
          {/* City & Clinic Selection */}
          <div>
            <h2 className="text-xl font-semibold mb-2">Select City & Clinic</h2>
            <select
              className="w-full p-3 rounded-md text-black mb-4"
              value={selectedCity}
              onChange={(e) => {
                setSelectedCity(e.target.value);
                setSelectedClinic(null);
                setSelectedDoctor(null);
              }}
            >
              <option value="">-- Choose City --</option>
              {cities.map((city) => (
                <option key={city.id} value={city.name}>{city.name}</option>
              ))}
            </select>

            {selectedCity && (
              <div className="space-y-3">
                {hospitals.map((clinic) => (
                  <button
                    key={clinic.id}
                    onClick={() => {
                      setSelectedClinic(clinic);
                      setSelectedDoctor(null);
                    }}
                    className={`w-full px-4 py-2 rounded-md transition text-left ${
                      selectedClinic?.id === clinic.id
                        ? "bg-white text-blue-900 font-bold"
                        : "bg-blue-500 hover:bg-blue-400"
                    }`}
                  >
                    {clinic.name}
                  </button>
                ))}
              </div>
            )}

            {selectedClinic && (
              <div className="mt-6">
                <p className="text-lg font-semibold">{selectedClinic.name}</p>
                <p className="text-sm text-gray-200 mb-2">{selectedClinic.address}</p>
                <a
                  href={`https://www.google.com/search?q=${encodeURIComponent(selectedClinic.name)}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-300 underline"
                >
                  ðŸ”— View on Google
                </a>
              </div>
            )}
          </div>

          {/* Doctor Selection */}
          {selectedClinic && doctors.length > 0 && (
            <div>
              <h2 className="text-xl font-semibold mb-3">Choose a Doctor</h2>
              <div className="space-y-3">
                {doctors.map((doctor) => {
                  const doctorPhoto = doctor.gender === "Female"
                    ? "/doctor_images/female_doctor.png"
                    : "/doctor_images/male_doctor.png";

                  return (
                    <div
                      key={doctor.id}
                      onClick={() => setSelectedDoctor(doctor)}
                      className={`p-4 rounded-md shadow-md cursor-pointer transition border bg-blue-700 ${
                        selectedDoctor?.id === doctor.id ? "border-white" : "border-transparent"
                      }`}
                    >
                      <div className="flex items-center gap-4">
                        <img src={doctorPhoto} alt={doctor.name} className="w-16 h-16 rounded-full object-cover" />
                        <div>
                          <h3 className="text-lg font-semibold">{doctor.name}</h3>
                          <p className="text-sm text-gray-200">{doctor.specialization}</p>
                          <p className="text-xs text-gray-300">{doctor.experienceYears} years experience</p>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>

        {/* Patient Info */}
        <div className="grid md:grid-cols-2 gap-4 mb-6">
          <input
            type="text"
            placeholder="Patient Name"
            value={patientName}
            onChange={(e) => setPatientName(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
          <input
            type="text"
            placeholder="Contact Number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            className="w-full p-3 rounded-md text-black"
          />
        </div>

        {/* Confirm Button */}
        {selectedDoctor && patientName && contactNumber && (
          <div className="text-center">
            <button
              onClick={handleConfirm}
              className="px-8 py-3 bg-green-500 hover:bg-green-400 text-white rounded-lg font-semibold"
            >
              Confirm Appointment
            </button>
            {confirmationMessage && (
              <p className="mt-4 text-green-300 font-medium">{confirmationMessage}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Booking;
